mapper.select:
select count(*) from recipe where state = 'y' and category = #{category}
and ${search} like '%'||#{keyword}||'%'
and (subject like '%'||#{keyword}||'%' or content like '%'||#{keyword}||'%')

SELECT * FROM ( SELECT ROWNUM rn, a.* FROM ( SELECT * FROM recipe WHERE state = 'y' and category = #{category}
and ${search} like '%'||#{keyword}||'%'
and subject like '%'||#{keyword}||'%' or content like '%'||#{keyword}||'%'
ORDER BY rnum desc) a ) WHERE rn BETWEEN #{startRow} AND #{endRow}
